import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NotificationForm from './components/NotificationForm';
import NotificationBell from './components/NotificationBell';
import Dashboard from './components/Dashboard';
import './App.css';

function App() {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);

  const fetchNotifications = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/notifications');
      setNotifications(response.data.data);
      setUnreadCount(response.data.unreadCount);
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

  const createNotification = async (notificationData) => {
    try {
      await axios.post('http://localhost:3001/api/notifications', notificationData);
      fetchNotifications();
    } catch (error) {
      console.error('Error creating notification:', error);
    }
  };

  const markAsRead = async (id) => {
    try {
      await axios.put(`http://localhost:3001/api/notifications/${id}/read`);
      fetchNotifications();
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      await axios.put('http://localhost:3001/api/notifications/read-all');
      fetchNotifications();
    } catch (error) {
      console.error('Error marking all notifications as read:', error);
    }
  };

  const deleteNotification = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/notifications/${id}`);
      fetchNotifications();
    } catch (error) {
      console.error('Error deleting notification:', error);
    }
  };

  useEffect(() => {
    fetchNotifications();
  }, []);

  return (
    <div className="app">
      <header className="app-header">
        <h1>Notification System</h1>
        <NotificationBell 
          notifications={notifications}
          unreadCount={unreadCount}
          onMarkAsRead={markAsRead}
          onMarkAllAsRead={markAllAsRead}
          onDelete={deleteNotification}
        />
      </header>
      
      <main className="app-main">
        <div className="form-section">
          <NotificationForm onSubmit={createNotification} />
        </div>
        
        <div className="dashboard-section">
          <Dashboard 
            notifications={notifications}
            onMarkAsRead={markAsRead}
            onDelete={deleteNotification}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
