import React, { useState } from 'react';
import './NotificationBell.css';

const NotificationBell = ({ notifications, unreadCount, onMarkAsRead, onMarkAllAsRead, onDelete, onViewAll }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const getTypeIcon = (type) => {
    switch (type) {
      case 'selected': return '‚úÖ';
      case 'rejected': return '‚ö†Ô∏è';
      case 'pending': return '‚ùå';
     case 'process': return 'üîÉ';
      default: return '‚ÑπÔ∏è';
    }
  };

  return (
    <div className="notification-bell">
      <button className="bell-button" onClick={toggleDropdown}>
        <span className="bell-icon">üîî</span>
        {unreadCount > 0 && (
          <span className="badge">{unreadCount}</span>
        )}
      </button>

      {isOpen && (
        <div className="notification-dropdown">
          <div className="dropdown-header">
            <h3>Notifications</h3>
            {notifications.length > 0 && (
              <button 
                className="mark-all-read"
                onClick={onMarkAllAsRead}
              >
                Mark all read
              </button>
            )}
          </div>

          <div className="notification-list">
            {notifications.length === 0 ? (
              <div className="no-notifications">
                <span>üì≠</span>
                <p>No notifications</p>
              </div>
            ) : (
              notifications.slice(0, 5).map((notification) => (
                <div 
                  key={notification.id} 
                  className={`notification-item ${!notification.read ? 'unread' : ''}`}
                >
                  <div className="notification-content">
                    <div className="notification-header">
                      <span className="type-icon">{getTypeIcon(notification.type)}</span>
                      <span className="title">{notification.title}</span>
                      <span className="time">{formatTime(notification.createdAt)}</span>
                    </div>
                    <p className="message">{notification.message}</p>
                  </div>
                  
                  <div className="notification-actions">
                    {!notification.read && (
                      <button 
                        className="mark-read-btn"
                        onClick={() => onMarkAsRead(notification.id)}
                        title="Mark as read"
                      >
                        ‚úì
                      </button>
                    )}
                    <button 
                      className="delete-btn"
                      onClick={() => onDelete(notification.id)}
                      title="Delete"
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>

          {notifications.length > 5 && (
            <div className="dropdown-footer">
              <p>+{notifications.length - 5} more notifications</p>
            </div>
          )}
          
          {notifications.length > 0 && (
            <div className="dropdown-footer">
              <button className="view-all-btn" onClick={onViewAll}>
                View All Notifications
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default NotificationBell;
